package bitwiseoperations;

public class BitWiseOperationsExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=4;
		int b=6;
		System.out.println("A & B = "+(a&b));
		System.out.println("A | B = "+(a^b));
		System.out.println("A ^ B = "+(a^b));
		System.out.println("~ A = "+(~ a));
		System.out.println("~ B = "+(~ b));
		System.out.println("A >> B = "+(a>>b));
		System.out.println("A << B = "+(a<<b));
		System.out.println("A >>> B = "+(a>>>b));
	
		
	}

}
